#Combien y a-t-il de handle dans cette array ?
def handles_length(handles)
    return handles.length
end

#Quel est le handle le plus court de cette liste ?
def shortest_handle(handles)
    return handles.min_by {|x| x.length }
end

#Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)
def five_str_handle(handles)
    return handles.count {|x| x.length == 6 }
end

#Combien commencent par une majuscule (première lettre juste après le @) ?
def uppercase_handle(handles)
    return handles.count {|x| x[1] =~ /[A-Z]/ }
end

#Trie la liste de handle par ordre alphabétique.
def alphabetical_order(handles)
    return handles.sort do |personne_a, personne_b| 
        personne_a <=> personne_b
    end.first(10).join(", ")
end

#Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)
def handle_size_min_to_max(handles)
    return handles.sort_by {|x| x.length}.first(20).join(", ")
end

#Quelle est la position dans l'array de la personne @epenser ?
def e_penser(handles)
    return handles.index("@epenser")
end

#Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)
def tri_caracteres(handles)
    for number in 1..(handles.max {|a, b| a.length <=> b.length}).length
            puts "Nombre de handles avec #{number} caractères : #{handles.count {|i| i.length == number}}"
    end
end


